css:
.app .prices__points {
  display:none;
}
.product__sale-timer {
  background-color:#333333;
}
.buy-together__item .product-card__buy {
  display: none !important;
}
.buy-together__title {
  position: relative;
  color: #37b8f8;
  font-family: quicksand;
  line-height: 1.22;
  text-decoration: none;
  margin-bottom: 1.25rem;
  > div {
    padding: 0 var(--spacer-3);
    margin: 0 auto;
    display: inline-block;
    background: #fff;
    font-size: 1.575rem;
    font-weight: 700;
    z-index: 2;
    position: relative;
  }
  &::before {
    background: var(--border-color);
    content: " ";
    height: var(--border-width);
    left: 15%;
    margin-top: -.05rem;
    position: absolute;
    top: 50%;
    width: 70%;
  }
}

footer * {
  color: #fff !important;
  background: #37b8f8;
}

.product-card .product-card__buy{
  opacity: 1!important;
  position: relative;
  top: auto;
  margin: 0;
  bottom: auto;
  display: block;
  left: auto;
  transform: unset;
  background: transparent!important;
  display: flex;
  align-items: center;
  gap: 15px;
}

.qtde-adicionar-carrinho{
  margin: 0;
  display: flex;
  align-items: center;
  text-align: center;
  border: 1px solid #f4f4f4;
  line-height: 36px!important;
  border-radius: 36px!important;
  width: auto!important;
  padding: 0 10px;
}

.qtde-adicionar-carrinho > input{
  -moz-appearance:textfield; /* Firefox */
  height: 36px;
  padding: 0;
  width:40px;
  border: 0!important;
  text-align:center
}
.qtde-adicionar-carrinho > button{
  border: 0;
  padding: 0;
  background: transparent;
}
.qtde-adicionar-carrinho > button > svg{
  height: 16px;
  margin: 0;
  display: block;
  width: 16px;
}

.qtde-adicionar-carrinho > input::-webkit-outer-spin-button,
.qtde-adicionar-carrinho > input::-webkit-inner-spin-button {
  /* display: none; <- Crashes Chrome on hover */
  -webkit-appearance: none;
  margin: 0; /* <-- Apparently some margin are still there even though it's hidden */
}

input[type=number] {
  -moz-appearance:textfield; /* Firefox */
}
.product-card__buy{margin-top:1rem!important}
.product-card__buy .btn-primary{
  padding: 0px;
  width: auto;
  min-width: unset;
  line-height: 36px;
}
.product-card__buy .btn-primary div{padding: 0 15px}

@media(max-width:990px){
  .product-card .product-card__buy{flex-direction: column;}
  .product-card .product-card__buy > *{width:100%!important;justify-content: space-between;}
}

.recommended-items .product-card__buy{
  display:flex!important;
  position:relative!important;
  flex-direction: column!important;
  transform: unset;
  width: auto;
  left: auto;
  right: auto;
}
.recommended-items .product-card__buy .btn-primary div{padding:0}

/* SEARCH BY VOICE */
#search-bar form > button:first-child{
  position: absolute;
  z-index: 2;
  height: 100%;
  border: 0;
  background: transparent;
  padding: 6px 10px 10px 10px;
  outline:none
}
#search-bar form > button + input{padding-left:35px;}
@keyframes flickerAnimation { /* flame pulses */
  0%   { opacity:1; }
  50%  { opacity:0; }
  100% { opacity:1; }
}
.fa-microphone-slash{
  opacity:1;  
  animation: flickerAnimation 1s infinite;
}
@media(max-width:990px){
  #search-bar form>button:first-child{padding:0px 10px;line-height: initial;}
}
