}

#clubeshow .content {

    background: #f8f8f8;

    padding: 1rem;

    margin-bottom: 2rem;

    box-shadow: 0 0 10px rgba(0,0,0,.25);

    max-height: 50vh;

    border-radius: 5px;

    position: relative;

    transform: scale(.1);

    opacity: 0;

    pointer-events: none;

    left: -450px;

    transition: all .5s;

    position: fixed;

    bottom: auto;

    top:50%;

    transform: translate(0, -50%)!important;

    max-width: calc(100vw - 70px);




}

