css:
.app .prices__points {
  display:none;
}
.product__sale-timer {
  background-color:#333333;
}
.buy-together__item .product-card__buy {
  display: none !important;
}
.buy-together__title {
  position: relative;
  color: #37b8f8;
  font-family: quicksand;
  line-height: 1.22;
  text-decoration: none;
  margin-bottom: 1.25rem;
  > div {
    padding: 0 var(--spacer-3);
    margin: 0 auto;
    display: inline-block;
    background: #fff;
    font-size: 1.575rem;
    font-weight: 700;
    z-index: 2;
    position: relative;
  }
  &::before {
    background: var(--border-color);
    content: " ";
    height: var(--border-width);
    left: 15%;
    margin-top: -.05rem;
    position: absolute;
    top: 50%;
    width: 70%;
  }
}

footer * {
  color: #fff !important;
  background: #37b8f8;
}
